import React, { useState, useEffect } from 'react';
import { Navbar, NavbarBrand, Button } from 'reactstrap';


export default function Menu(props) {
  useEffect(() => {
    props.categoria();
  }, []);
  useEffect(() => {
    props.generar();
  }, []);


  const [numero, setNumero] = useState('');

  const handleClick = (id) => {
    setNumero(id); // Almacenar el ID del botón clicado en la variable 'numero'
    props.menuelegir(id); // Llamar a la función para manejar el menú elegido con el valor de 'id'
  };

  return (
    <div>
      <Navbar className="menu" id="menu">
        <h2 href="/">Burger Delicias</h2>
        <div id="menu2"> 
          {props.menu.map(v => (
            <div key={v.id_categoria} id='boton'>
              <Button
                block
                color="dark"
                outline
                size="lg"
                className='btnmenu'
                onClick={() => handleClick(v.id_categoria)} // Llamar a handleClick con el ID del botón clicado
              >
                {v.nombre} <br />
                <img src={`${process.env.PUBLIC_URL}/${v.img}`} className="entrante"/>
              </Button>
              <br />
            </div>
          ))}
        </div>
      </Navbar>
    </div>
  );
}
