import './App.css';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';
import Menu from './componentes/menu';
import Carrito from './componentes/carrito';
import Logo from './componentes/logo';
import { Component, useEffect } from 'react';
import Tostada from './componentes/toast';
import toast, { Toaster } from "react-hot-toast";
import axios from 'axios';
import Catalogo from './componentes/catalogo.js';
import { PHPSENTENCIAS, PHPTODOSPEDIDOS, PHPVER } from './php/Datos.js';
import { PHPCATEGORIA } from './php/Datos.js';

import { PHPINGREDIENTES } from './php/Datos.js';



class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      menuItem: "",
      productos: [],
      categoria: [],
      logo: true,
      pedido: true,
      carrito: [],
      precioTotal: 0,
      ingredientes: [],
      cantidadTotal: 0,
      show: true,
      telefono: '',
      lp:false,
      id: [],
      swalWithBootstrapButtons: Swal.mixin({
        customClass: {
          confirmButton: "btn btn-success",
          cancelButton: "btn btn-danger"
        },
        buttonsStyling: false
      }),
      datospedido: [],
      datosfilt: [],
      todospedidos:[],
      estadopedido:[],

    }
  }
  componentDidMount(){
    this.todospedidos();
  }




  todospedidos() {
    axios.get(PHPTODOSPEDIDOS)
      .then(response => {
        let nuevosPedidos = response.data;
        console.log(nuevosPedidos);

        this.setState({ todospedidos: nuevosPedidos });
      })
      .catch(error => {
        console.error('Error al obtener todos los pedidos:', error);
      });
  }



 //aqui me traigo el json de todos los productos
  productos() {
    axios.get(PHPSENTENCIAS)
      .then(response => {
        this.setState({ productos: response.data });

      })
  }

  //aqui me traigo el json de los ingredientes de los productos
  ingredientes() {
    axios.get(PHPINGREDIENTES)
      .then(response => {
        this.setState({ ingredientes: response.data });

      })
  }

  //esto es una alerta con un pluggin que llama a la ventana cuando finaliza la compra y te da la opcion de cancelar el pedido o ver los detalles del pedido
  SweetAlert2(id, datos) {

    Swal.fire({
      title: "Pedido realizado con éxito!",
      text: "La cocina empezará a preparar su pedido.",
      imageUrl: "https://i.gifer.com/ZXFj.gif",
      imageWidth: 400,
      imageHeight: 300,
      imageAlt: "Custom image",
      icon: "success",
      showCancelButton: true,
      confirmButtonText: "Ver mi pedido",
      cancelButtonText: "Cancelar pedido",
      reverseButtons: true
    }).then((result) => {
      if (result.isConfirmed) {
        let nombre = '';
        let telefono = '';
        let productos = '';
        let precio = '';
        let estado='';
        let idp='';

        Swal.fire({
          title: "Numero de pedido: " + idp,
          imageUrl: "https://i.gifer.com/TwuB.gif",
          imageWidth: 400,
          imageHeight: 300,
          html: `<p>Estado del pedido <strong>${estado}</strong></p>
                 <p>Nombre: ${nombre}</p>
                 <p>Teléfono: ${telefono}</p>
                 <p>Productos<ul> ${productos} </ul></p>
                 <p>Precio: ${precio} €</p>`,
          icon: "info"
        });
      } else if (result.dismiss === Swal.DismissReason.cancel) {
        console.log(id.ult_id);
        this.cancelarpedido(id.ult_id); // Llama a la función para cancelar el pedido
        Swal.fire({
          title: "Pedido cancelado",
          text: "Tu pedido ha sido cancelado con éxito.",
          icon: "success"
        });
      }
    });
  }


//aqui guardo el numero del menu que usare para el switch
  changeMenu(item) {
    let l = item
    console.log(item)
    this.setState({ menuItem: l, logo: false,lp:true })
  }

  //aqui me traigo el json de las categorias
  categorias() {
    axios.get(PHPCATEGORIA)
      .then(response => {
        let l = response.data
        this.setState({ categoria: l });

      })

  }


  render() {
    let obj = <Logo />;
    let bt=<></>;
    if (!this.state.logo) {
      obj = <><Catalogo
        productos={() => this.productos()}
        ingrediente={this.state.ingredientes}
        ingredientes={() => this.ingredientes()}
        id={this.state.menuItem}
        producto={this.state.productos}
        añadir={(nombre, precio, cantidad, id_pro) => this.añadirLista(nombre, precio, cantidad, id_pro)}

      /></>;
    }

    return (
      <div className='App vh-100 bg-dark'>
        <Menu
          categoria={() => this.categorias()}
          menu={this.state.categoria}
          menuelegir={(item) => this.changeMenu(item)}
          menuItem={this.state.menuItem}
          generar={()=>this.todospedidos()}

        />
        <div id='logocentrar'>
          {obj}
          {bt}
        </div>

        <Tostada />
        <Carrito
          mostrar={this.state.carrito}
          borrar={(lista) => this.borrar(lista)}
          total={this.state.precioTotal}
          canT={this.state.cantidadTotal}
          click={(n, t) => this.realizarpedido(n, t)}
        />

      </div>
    );
  }

}

export default App;
